import{_ as e}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as n,c as s,a,b as i,e as g,d as t,r}from"./app.eb6bd936.js";const d={},c=t('<h1 id="_36-张图梳理-intellij-idea-常用设置" tabindex="-1"><a class="header-anchor" href="#_36-张图梳理-intellij-idea-常用设置" aria-hidden="true">#</a> 36 张图梳理 Intellij IDEA 常用设置</h1><h2 id="_1-显示工具条" tabindex="-1"><a class="header-anchor" href="#_1-显示工具条" aria-hidden="true">#</a> 1. 显示工具条</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224203514537.jpg" alt="在这里插入图片描述" loading="lazy"> （2）设置方法</p><ul><li>标注1：View–&gt;Toolbar</li><li>标注2：View–&gt;Tool Buttons</li></ul><h2 id="_2-设置鼠标悬浮提示" tabindex="-1"><a class="header-anchor" href="#_2-设置鼠标悬浮提示" aria-hidden="true">#</a> 2.设置鼠标悬浮提示</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224203557930.jpg" alt="在这里插入图片描述" loading="lazy"> （2)设置方法</p><p>File–&gt;settings–&gt;Editor–&gt;General–&gt;勾选Show quick documentation… <img src="https://img-blog.csdnimg.cn/20210224203618871.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_3-显示方法分隔符" tabindex="-1"><a class="header-anchor" href="#_3-显示方法分隔符" aria-hidden="true">#</a> 3.显示方法分隔符</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224203648505.jpg" alt="在这里插入图片描述" loading="lazy"> （2)设置方法</p><p>File–&gt;settings–&gt;Editor–&gt;Appearance–&gt;勾选 <img src="https://img-blog.csdnimg.cn/20210224203711703.jpg" alt="q" loading="lazy"></p><h2 id="_4-忽略大小写提示" tabindex="-1"><a class="header-anchor" href="#_4-忽略大小写提示" aria-hidden="true">#</a> 4.忽略大小写提示</h2><p>（1）效果图</p><p>备注：idea的默认设置是严格区分大小写提示的，例如输入string不会提示String，不方便编码 <img src="https://img-blog.csdnimg.cn/20210224203811694.jpg" alt="在这里插入图片描述" loading="lazy"> （2）设置方法File–&gt;settings–&gt;Editor–&gt;General --&gt;Code Completion --&gt; <img src="https://img-blog.csdnimg.cn/20210224203830493.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_5-主题设置" tabindex="-1"><a class="header-anchor" href="#_5-主题设置" aria-hidden="true">#</a> 5.主题设置</h2><p>（1）效果图</p><p>备注：有黑白两种风格 <img src="https://img-blog.csdnimg.cn/20210224203904545.jpg" alt="在这里插入图片描述" loading="lazy"><img src="https://img-blog.csdnimg.cn/20210224203916562.jpg" alt="在这里插入图片描述" loading="lazy"> （2)设置方法</p><p>File–&gt;settings–&gt;Appearance &amp; Behavior–&gt;Appearance–&gt; <img src="https://img-blog.csdnimg.cn/20210224203938251.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_6-护眼主题设置" tabindex="-1"><a class="header-anchor" href="#_6-护眼主题设置" aria-hidden="true">#</a> 6.护眼主题设置</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224204009507.jpg" alt="在这里插入图片描述" loading="lazy"> （2)设置方法</p>',19),o=a("img",{src:"https://img-blog.csdnimg.cn/20210224204028538.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),h={href:"http://color-themes.com/?view=index&layout=Generic&order=popular&search=&page=1",target:"_blank",rel:"noopener noreferrer"},p=a("img",{src:"https://img-blog.csdnimg.cn/20210224204049458.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),m=a("img",{src:"https://img-blog.csdnimg.cn/20210224204104138.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),_=a("img",{src:"https://img-blog.csdnimg.cn/20210224204116351.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),b=a("img",{src:"https://img-blog.csdnimg.cn/20210224204138424.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),u=t('<h2 id="_7-自动导入包" tabindex="-1"><a class="header-anchor" href="#_7-自动导入包" aria-hidden="true">#</a> 7. 自动导入包</h2><p>(1）效果图 备注：默认情况是需要手动导入包的，比如我们需要导入Map类，那么需要手动导入，如果不需要使用了，删除了Map的实例，导入的包也需要手动删除，设置了这个功能这个就不需要手动了，自动帮你实现自动导入包和去包，不方便截图，效果请亲测~ （2）设置方法</p><p>File–&gt;settings–&gt;Editor–&gt;general–&gt;Auto Import–&gt; <img src="https://img-blog.csdnimg.cn/2021022420421999.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_8-单行显示多个tabs" tabindex="-1"><a class="header-anchor" href="#_8-单行显示多个tabs" aria-hidden="true">#</a> 8.单行显示多个Tabs</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224204304589.jpg" alt="默认是显示单排的Tabs:" loading="lazy"></p><p>单行显示多个Tabs: <img src="https://img-blog.csdnimg.cn/20210224204337379.jpg" alt="在这里插入图片描述" loading="lazy"> （2）设置方法 File–&gt;settings–&gt;Editor–&gt;General --&gt;Editor Tabs–&gt;去掉√ <img src="https://img-blog.csdnimg.cn/20210224204420119.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_9-设置字体" tabindex="-1"><a class="header-anchor" href="#_9-设置字体" aria-hidden="true">#</a> 9.设置字体</h2><p>（1）效果图 备注：默认安装启动Idea字体很小，看着不习惯，需要调整字体大小与字体（有需要可以调整） （2）设置方法 File–&gt;settings–&gt;Editor–&gt;Font–&gt; <img src="https://img-blog.csdnimg.cn/20210224204505784.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_10-配置类文档注释信息和方法注释模版" tabindex="-1"><a class="header-anchor" href="#_10-配置类文档注释信息和方法注释模版" aria-hidden="true">#</a> 10.配置类文档注释信息和方法注释模版</h2>',9),y=a("img",{src:"https://img-blog.csdnimg.cn/20210224204545258.jpg",alt:"在这里插入图片描述",loading:"lazy"},null,-1),j=a("img",{src:"https://img-blog.csdnimg.cn/20210224204604611.png",alt:"在这里插入图片描述",loading:"lazy"},null,-1),z={href:"https://blog.csdn.net/zeal9s/article/details/83514565",target:"_blank",rel:"noopener noreferrer"},f=t('<h2 id="_11-水平或者垂直显示代码" tabindex="-1"><a class="header-anchor" href="#_11-水平或者垂直显示代码" aria-hidden="true">#</a> 11.水平或者垂直显示代码</h2><p>（1）效果图 备注：Eclipse如果需要对比代码，只需要拖动Tabs即可，但是idea要设置 <img src="https://img-blog.csdnimg.cn/20210224204734417.jpg" alt="在这里插入图片描述" loading="lazy"> （2）设置方法</p><p>鼠标右击Tabs <img src="https://img-blog.csdnimg.cn/20210224204757375.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_12-更换快捷键" tabindex="-1"><a class="header-anchor" href="#_12-更换快捷键" aria-hidden="true">#</a> 12. 更换快捷键</h2><p>（1）效果图</p><p>备注：从Eclipse移植到idea编码，好多快捷键不一致，导致编写效率降低，现在我们来更换一下快捷键 （2）设置方法</p><ul><li><p>方法一： File–&gt;Setting–&gt; <img src="https://img-blog.csdnimg.cn/20210224204900966.jpg" alt="在这里插入图片描述" loading="lazy"> 例如设置成Eclipse的，设置好了之后可以ctrl+d删除单行代码（idea是ctrl+y）</p></li><li><p>方法二：设置模板 File–&gt;Setting–&gt; <img src="https://img-blog.csdnimg.cn/20210224204938310.jpg" alt="在这里插入图片描述" loading="lazy"></p></li><li><p>方法三： <img src="https://img-blog.csdnimg.cn/20210224205002231.jpg" alt="在这里插入图片描述" loading="lazy"> 以ctrl+o重写方法</p></li><li><p>为例 <img src="https://img-blog.csdnimg.cn/20210224205019981.jpg" alt="在这里插入图片描述" loading="lazy"></p></li></ul><h2 id="_13-注释去掉斜体" tabindex="-1"><a class="header-anchor" href="#_13-注释去掉斜体" aria-hidden="true">#</a> 13.注释去掉斜体</h2><p>（1）效果图 <img src="https://img-blog.csdnimg.cn/20210224205113664.jpg" alt="在这里插入图片描述" loading="lazy"> （2）设置方法</p><p>File–&gt;settings–&gt;Editor–&gt; <img src="https://img-blog.csdnimg.cn/20210224205131716.jpg" alt="在这里插入图片描述" loading="lazy"><img src="https://img-blog.csdnimg.cn/202102242051459.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_14-代码检测警告" tabindex="-1"><a class="header-anchor" href="#_14-代码检测警告" aria-hidden="true">#</a> 14.代码检测警告</h2><h2 id="提示等级设置" tabindex="-1"><a class="header-anchor" href="#提示等级设置" aria-hidden="true">#</a> 提示等级设置</h2><p><img src="https://img-blog.csdnimg.cn/20210224205204818.jpg" alt="在这里插入图片描述" loading="lazy"> 强烈建议，不要给关掉，不要嫌弃麻烦，他的提示都是对你好，帮助你提高你的代码质量，很有帮助的</p><h2 id="_15-项目目录相关–折叠空包" tabindex="-1"><a class="header-anchor" href="#_15-项目目录相关–折叠空包" aria-hidden="true">#</a> 15.项目目录相关–折叠空包</h2><p><img src="https://img-blog.csdnimg.cn/20210224205220446.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_16-窗口复位" tabindex="-1"><a class="header-anchor" href="#_16-窗口复位" aria-hidden="true">#</a> 16. 窗口复位</h2><p><img src="https://img-blog.csdnimg.cn/20210224205236946.jpg" alt="在这里插入图片描述" loading="lazy"> 这个就是当你把窗口忽然间搞得乱七八糟的时候，还可以挽回，就是直接restore一下，就好啦。</p><h2 id="_17-查看本地代码历史" tabindex="-1"><a class="header-anchor" href="#_17-查看本地代码历史" aria-hidden="true">#</a> 17. 查看本地代码历史</h2><p><img src="https://img-blog.csdnimg.cn/2021022420530438.jpg" alt="在这里插入图片描述" loading="lazy"></p><h2 id="_18-快速补全分号" tabindex="-1"><a class="header-anchor" href="#_18-快速补全分号" aria-hidden="true">#</a> 18. 快速补全分号</h2><p>CTRL + SHIFT + ENTER 在当前行任何地方可以快速在末尾生成分号</p><h2 id="_19-快速找到controller方法" tabindex="-1"><a class="header-anchor" href="#_19-快速找到controller方法" aria-hidden="true">#</a> 19. 快速找到Controller方法</h2><p>如果你的项目里有非常多的controller，里面有非常多的http或者resful方法。如何快速找到这些方法呢？这个时候，ctrl+alt+shift+n就可以派上用场了。 比如说，你依稀记得入账单相关的接口，都有个bill的url路径，那么使用ctrl+alt+shift+n后，直接输入/bill即可。</p><p><img src="https://img-blog.csdnimg.cn/20210224205514795.png" alt="在这里插入图片描述" loading="lazy"> 当你在成千上万的Controller里寻找方法时，这一招就可以大大提高效率。</p><h2 id="_20-大括号匹配" tabindex="-1"><a class="header-anchor" href="#_20-大括号匹配" aria-hidden="true">#</a> 20.大括号匹配</h2><p>这个也非常有用，因为代码太长，某个for循环，可能已经撑满整个屏幕了。这个时候，找到某个大括号对应的另外一边就很费劲。你可以将光标定位在某个大括号一边，然后使用ctrl+]或者ctrl+[来回定位即可。 <strong>补充</strong>：以上的配置信息都保存在系统盘的 <img src="https://img-blog.csdnimg.cn/20210224205558296.png" alt="在这里插入图片描述" loading="lazy"> 默认会有这两个文件 <img src="https://img-blog.csdnimg.cn/20210224205626669.png" alt="在这里插入图片描述" loading="lazy"></p><p>config:在初始化安装IntelliJ IDEA时有询问你是否导入以存在的配置信息，这个config就是你的配置信息，方便更换电脑和换系统的时候重新安装，前提是要保存好此文件夹。 system:此文件夹是IntelliJ IDEA发生什么不可预知性的错误时，比如蓝屏，突然断电导致项目不能启动，可以尝试删除此文件，让系统重新生成一个system的文件</p>',27);function x(E,I){const l=r("ExternalLinkIcon");return n(),s("div",null,[c,a("p",null,[i("如果想将编辑页面变换主题，可以去设置里面调节背景颜色 "),o,i(" 如果需要很好看的编码风格，这里有很多主题 "),a("a",h,[i("http://color-themes.com/?view=index&layout=Generic&order=popular&search=&page=1"),g(l)]),i(" 点击相应主题，往下滑点击按钮 "),p,i(" 下载下来有很多Jar包 "),m,_,i(" 在上面的位置选择导入jar包，然后重启idea生效，重启之后去设置 "),b]),u,a("p",null,[i("（1）效果图 备注：团队开发时方便追究责任与管理查看 "),y,j,i(" （2）设置方法 "),a("a",z,[i("https://blog.csdn.net/zeal9s/article/details/83514565"),g(l)])]),f])}const A=e(d,[["render",x],["__file","36张图梳理 Intellij IDEA常用设置.html.vue"]]);export{A as default};
