import{_ as p}from"./_plugin-vue_export-helper.cdc0426e.js";import{o,c as t,d as r}from"./app.bba02735.js";const n={},s=r('<h1 id="\u4E92\u8054\u7F51\u91D1\u878D\u884C\u4E1A\u540D\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u4E92\u8054\u7F51\u91D1\u878D\u884C\u4E1A\u540D\u8BCD" aria-hidden="true">#</a> \u4E92\u8054\u7F51\u91D1\u878D\u884C\u4E1A\u540D\u8BCD</h1><h2 id="_1-\u8D37\u8BB0-\u501F\u8BB0\u4E1A\u52A1" tabindex="-1"><a class="header-anchor" href="#_1-\u8D37\u8BB0-\u501F\u8BB0\u4E1A\u52A1" aria-hidden="true">#</a> 1. \u8D37\u8BB0/\u501F\u8BB0\u4E1A\u52A1</h2><h3 id="\u8D37\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u8D37\u8BB0" aria-hidden="true">#</a> \u8D37\u8BB0</h3><p>\u8D37\u8BB0\u4E1A\u52A1\u662F\u4ED8\u6B3E\u884C\u6216\u8005\u4ED8\u6B3E\u4EBA\u4E3B\u52A8\u5411\u6536\u6B3E\u884C\u6216\u8005\u6536\u6B3E\u4EBA\u53D1\u8D77\u7684\u4ED8\u6B3E\u4E1A\u52A1\u3002\u201C\u8D37\u201D\u5B57\u662F\u6307\u8D44\u4EA7\u65B9\u7684\u51CF\u5C11\u6216\u8005\u8D1F\u503A\u7684\u589E\u52A0\uFF0C\u6700\u5E38\u89C1\u7684\u5C31\u662F\u8D37\u8BB0\u5361\uFF0C\u4E5F\u5C31\u662F\u4FE1\u7528\u5361\u3002\u5E73\u65F6\u6211\u4EEC\u7528\u4FE1\u7528\u5361\u65F6\uFF0C\u989D\u5EA6\u51CF\u5C11\uFF0C\u8D1F\u503A\u589E\u52A0\u4E86\uFF0C\u6240\u4EE5\u53EB\u505A\u8D37\u8BB0\u5361\u3002\u6BD4\u5982\u4F60\u4ECE\u94F6\u884C\u8F6C\u8D26\u5230\u522B\u7684\u94F6\u884C\uFF0C\u6216\u8005\u4ECE\u4F01\u4E1A\u8D26\u6237\u4EE3\u4ED8\u5DE5\u8D44\u5230\u5404\u4E2A\u5458\u5DE5\u8D26\u6237\uFF0C\u5BF9\u4E8E\u4ED8\u6B3E\u884C\u6765\u8BF4\u94F6\u884C\u7684\u8D44\u4EA7\u90FD\u51CF\u5C11\u4E86\uFF0C\u90FD\u662F\u8D37\u8BB0\u4E1A\u52A1\u3002</p><p>\u592E\u884C\u4F53\u7CFB\u91CC\u9762\u5206\u4E3A\u666E\u901A\u8D37\u8BB0\u3001\u5B9A\u671F\u8D37\u8BB0\u3001\u5B9E\u65F6\u8D37\u8BB0\u4E1A\u52A1 :</p><p>\uFF08\u4E00\uFF09\u666E\u901A\u8D37\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982\u8F6C\u8D26\uFF0C\u4ECE\u8001\u738B\u7684\u5DE5\u5546\u94F6\u884C\u8F6C\u8D26\u5230\u8001\u674E\u7684\u519C\u4E1A\u94F6\u884C\uFF1B</p><p>\uFF08\u4E8C\uFF09\u5B9A\u671F\u8D37\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982\u4EE3\u4ED8\u5DE5\u8D44\uFF0C\u6BCF\u6708\u5B9A\u671F\u4ED8\u85AA\u6C34\u7ED9\u5458\u5DE5\uFF1B</p><p>\uFF08\u4E09\uFF09\u5B9E\u65F6\u8D37\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982\u94F6\u884C\u5B58\u6B3E\uFF0C\u5B9E\u65F6\u8FDB\u8D26\uFF1B</p><h3 id="\u501F\u8BB0" tabindex="-1"><a class="header-anchor" href="#\u501F\u8BB0" aria-hidden="true">#</a> \u501F\u8BB0</h3><p>\u501F\u8BB0\u4E1A\u52A1\u662F\u6536\u6B3E\u884C\u6216\u8005\u6536\u6B3E\u4EBA\u4E3B\u52A8\u5411\u4ED8\u6B3E\u884C\u6216\u8005\u4ED8\u6B3E\u4EBA\u53D1\u8D77\u7684\u6263\u6B3E\u4E1A\u52A1\u3002\u201C\u501F\u201D\u5B57\u662F\u6307\u8D44\u4EA7\u65B9\u7684\u589E\u52A0\u6216\u8005\u8D1F\u503A\u7684\u51CF\u5C11\uFF0C\u6700\u5E38\u89C1\u7684\u5C31\u662F\u501F\u8BB0\u5361\uFF0C\u4E5F\u5C31\u662F\u50A8\u84C4\u5361\u3002\u5E73\u65F6\u6211\u4EEC\u7528\u50A8\u84C4\u5361\u65F6\uFF0C\u4E0D\u80FD\u900F\u652F\uFF0C\u53EA\u6709\u6709\u94B1\uFF0C\u5C31\u662F\u8D44\u4EA7\uFF0C\u6240\u4EE5\u53EB\u505A\u501F\u8BB0\u5361\u3002\u6BD4\u5982\u4F60\u5728\u94F6\u884C\u50A8\u84C4\uFF0C\u6536\u5230\u5DE5\u8D44\u5230\u8D26\uFF0C\u5BF9\u4E8E\u4E2A\u4EBA\u6765\u8BF4\u94F6\u884C\u7684\u8D44\u4EA7\u591A\u4E86\uFF0C\u90FD\u662F\u501F\u8BB0\u4E1A\u52A1\u3002\u4F1A\u8BA1\u79D1\u76EE\u91CC\u9762\uFF0C\u5BF9\u4E8E\u4E2A\u4EBA\u6765\u8BF4\uFF0C \u501F\uFF1A\u94F6\u884C\u5B58\u6B3E \u8D37\uFF1A\u73B0\u91D1\u3002</p><p>\u592E\u884C\u4F53\u7CFB\u91CC\u9762\u5206\u4E3A\u666E\u901A\u501F\u8BB0\u3001\u5B9A\u671F\u501F\u8BB0\u3001\u5B9E\u65F6\u501F\u8BB0\u4E1A\u52A1 \uFF1A \u200B \uFF08\u4E00\uFF09\u666E\u901A\u501F\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982\u8D44\u91D1\u5F52\u96C6\uFF0C\u4E00\u53D1\u5DE5\u8D44\uFF0C\u8001\u738B\u7684\u62DB\u884C\u5DE5\u8D44\u5C31\u88AB\u8001\u5A46\u6263\u5230\u5979\u519C\u884C\u8D26\u6237\uFF1B</p><p>\uFF08\u4E8C\uFF09\u5B9A\u671F\u501F\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982\u4EE3\u6536\u4FDD\u9669\u8D39\u7528\uFF0C\u4E00\u5E74\u4E00\u6B21\u5B9A\u671F\u53BB\u8001\u738B\u7684\u62DB\u884C\u6263\u6B3E\uFF1B</p><p>\uFF08\u4E09\uFF09\u5B9E\u65F6\u501F\u8BB0\u4E1A\u52A1\uFF0C\u6BD4\u5982ATM\u53D6\u94B1\uFF0C\u4ECE\u5B58\u6B3E\u91CC\u9762\u5B9E\u65F6\u6263\u9664\u4E86\uFF0C\u53D8\u6210\u73B0\u91D1\u5230\u4E86\u624B\u4E0A\uFF1B</p><h3 id="\u6765\u8D26\u3001\u5F80\u8D26" tabindex="-1"><a class="header-anchor" href="#\u6765\u8D26\u3001\u5F80\u8D26" aria-hidden="true">#</a> \u6765\u8D26\u3001\u5F80\u8D26</h3><p>\u5206\u4E3A\uFF1A\u501F\u8BB0\u6765\u5E10\uFF0C\u501F\u8BB0\u5F80\u8D26\uFF0C\u8D37\u8BB0\u6765\u5E10\uFF0C\u8D37\u8BB0\u5F80\u8D26</p><p>\u6765\u8D26\uFF1A\u63A5\u6536\u4E1A\u52A1 \u5F80\u5E10\uFF1A\u53D1\u9001\u4E1A\u52A1\uFF1B\u501F\u5C31\u662F\u6536\u6B3E\uFF0C\u8D37\u5C31\u662F\u4ED8\u6B3E\u3002</p><p>(a) \u501F\u8BB0\u5F80\u5E10\uFF1A\u6211\u884C\u95EE\u4ED6\u884C\u8981\u94B1\uFF08\u53D1\u62A5\uFF09</p><p>(b) \u501F\u8BB0\u6765\u8D26\uFF1A\u4ED6\u884C\u95EE\u6211\u884C\u8981\u94B1\uFF08\u6536\u62A5\uFF09</p><p>\xA9 \u8D37\u8BB0\u5F80\u5E10\uFF1A\u6211\u884C\u7ED9\u4ED6\u884C\u6253\u94B1\uFF08\u53D1\u62A5\uFF09</p><p>(d) \u8D37\u8BB0\u6765\u8D26\uFF1A\u4ED6\u884C\u7ED9\u6211\u884C\u6253\u94B1\uFF08\u6536\u62A5\uFF09</p><p>\u5373\u6765\u8D26\u90FD\u662F\u6536\u62A5\uFF0C\u5F80\u8D26\u90FD\u662F\u53D1\u62A5</p><p>\u5DE5\u5546\u94F6\u884CA \uFF08\u4ED8\u6B3E\uFF09------\u6253\u94B1------\u300B\u62DB\u5546\u94F6\u884CB\uFF08\u6536\u6B3E\uFF09 (\u8D37\u8BB0\u5F80\u8D26) (\u8D37\u8BB0\u6765\u5E10) \u5DE5\u5546\u94F6\u884CA\uFF08\u6536\u6B3E\uFF09-----\u8981\u94B1------\u300B\u62DB\u5546\u94F6\u884CB\uFF08\u4ED8\u6B3E\uFF09 (\u501F\u8BB0\u5F80\u8D26) (\u501F\u8BB0\u6765\u5E10)</p><h2 id="_2-\u94F6\u884C\u652F\u4ED8\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_2-\u94F6\u884C\u652F\u4ED8\u7CFB\u7EDF" aria-hidden="true">#</a> 2. \u94F6\u884C\u652F\u4ED8\u7CFB\u7EDF</h2><p><strong>\u4E2D\u56FD\u73B0\u4EE3\u5316\u652F\u4ED8\u6E05\u7B97\u7CFB\u7EDF \uFF08CNAPS\uFF09</strong></p><p>\u4E2D\u56FD\u7684\u652F\u4ED8\u6E05\u7B97\u6709\u4E24\u5957\u4F53\u7CFB</p><ul><li>\u5F53\u4F60\u53BBATM\u53D6\u94B1\u65F6\uFF0C\u7528\u7684\u662F\u592E\u884C\u7684CNAPS\uFF08\u4E2D\u56FD\u73B0\u4EE3\u5316\u652F\u4ED8\u6E05\u7B97\u7CFB\u7EDF\uFF09</li><li>\u5F53\u4F60\u7528\u652F\u4ED8\u5B9D\u4E70\u4E70\u4E70\u65F6\uFF0C\u7528\u7684\u662F\u7B2C\u4E09\u65B9\u652F\u4ED8\u7684\u6E05\u7B97\u7CFB\u7EDF</li></ul><h3 id="_1-cnaps\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#_1-cnaps\u4ECB\u7ECD" aria-hidden="true">#</a> (1) CNAPS\u4ECB\u7ECD</h3><p><strong>\u5F53\u4F60\u5728ATM\u53D6\u94B1\u65F6\uFF0C\u94F6\u884C\u90FD\u505A\u4E86\u4EC0\u4E48\uFF1F</strong></p><p>\u6211\u4EEC\u5148\u62FF\u4E00\u4E2AATM\u53D6\u94B1\u7684\u4F8B\u5B50\u6765\u8BF4\u660E\u8DE8\u884C\u4E1A\u52A1\u7684\u8FC7\u7A0B\u3002</p><p>\u5047\u5982\u4F60\u62FF\u7740\u4E00\u5F20\u5DE5\u884C\u5361\u53BB\u5EFA\u884C\u7684ATM\u53D6\u4E86100\u5143\uFF0C\u8FD9\u65F6\u5019\u5C31\u53D1\u751F\u4E86\u8DE8\u884C\u4E1A\u52A1\u3002</p><p>\u8FD9\u4E2A\u8FC7\u7A0B\u975E\u5E38\u7B80\u5355\uFF1A</p><blockquote><p><strong>\u5EFA\u884C\u7CFB\u7EDF\u544A\u8BC9\u5DE5\u884C\u7CFB\u7EDF\uFF0C\u6709\u4E2A\u5DE5\u884C\u7528\u6237\u8981\u5728\u6211\u8FD9\u513F\u53D6100\u5143\uFF0C\u80FD\u4E0D\u80FD\u8BA9\u4ED6\u53D6\uFF1F</strong></p><p><strong>\u5DE5\u884C\u8BF4\uFF0C\u4ED6\u7684\u5DE5\u884C\u8D26\u6237\u591F\u6263100\u5143\uFF0C\u4F60\u5148\u5E2E\u6211\u7ED9\u4E86\u5427\uFF01</strong></p><p><strong>\u5EFA\u884CATM\u5C31\u5410\u51FA100\u5143\u7ED9\u4F60\uFF1B</strong></p></blockquote><p>\u4E8E\u662F\uFF0C\u4F60\u62FF\u5230\u4E86\u73B0\u91D1\uFF0C\u540C\u65F6\u4F60\u7684\u5DE5\u884C\u8D26\u6237\u4E5F\u51CF\u5C11\u4E86100\u5143\u3002\u5BF9\u4F60\u800C\u8A00\uFF0C\u6574\u4E2A\u4EA4\u6613\u5C31\u5DF2\u7ECF\u7ED3\u675F\u4E86\u3002\u4F46\u662F\uFF0C\u5EFA\u884C\u5148\u66FF\u5DE5\u884C\u7ED9\u4E86\u4F60100\u5143\uFF0C\u8FD9\u91CC\u5F62\u6210\u4E86\u4E00\u4E2A\u94F6\u884C\u95F4\u7684\u503A\u52A1\u5173\u7CFB\uFF1A\u5DE5\u884C\u6B20\u5EFA\u884C100\u5143\u3002\u8FD9100\u5143\u4F55\u65F6\u8FD8\uFF0C\u600E\u4E48\u8FD8\uFF0C\u867D\u8BF4\u662F\u94F6\u884C\u4E4B\u95F4\u7684\u4E8B\uFF0C\u4F46\u662F\u53EA\u6709\u7B49\u8FD9100\u5143\u8FD8\u6E05\u4E86\uFF0C\u94F6\u884C\u624D\u4F1A\u628A\u8FD9\u7B14\u4EA4\u6613\u5F53\u505A\u662F\u771F\u7684\u5B8C\u7ED3\u4E86\u3002\u53EF\u89C1\uFF0C\u94F6\u884C\u7684\u4E00\u6B21\u8DE8\u884C\u53D6\u6B3E\u53EF\u4EE5\u5206\u4E3A\u652F\u4ED8\u548C\u6E05\u7B97\u4E24\u4E2A\u6B65\u9AA4\u3002</p><p><strong>\u652F\u4ED8\u548C\u6E05\u7B97\u5C31\u662F\u4FE1\u606F\u6D41\u548C\u8D44\u91D1\u6D41</strong></p><ul><li><p>\u4ECE\u4F60\u585E\u5361\u5230ATM\u5410\u51FA\u949E\u7968\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u79F0\u4E3A<strong>\u652F\u4ED8</strong>\uFF08Payment\uFF09</p></li><li><p>\u4ECE\u4F60\u585E\u5361\u5230ATM\u5410\u51FA\u949E\u7968\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u79F0\u4E3A<strong>\u652F\u4ED8</strong>\uFF08Payment\uFF09</p><p>\u652F\u4ED8\u53CD\u6620\u7684\u662F\u4EA4\u6613\u7684\u4FE1\u606F\u6D41\uFF0C\u4E00\u822C\u90FD\u662F\u5B9E\u65F6\u7684\u3002\u5F53\u4F60\u5728\u5EFA\u884CATM\u53D6\u6B3E\u65F6\uFF0C\u5DE5\u5546\u94F6\u884C\u5C42\u9762\u53EA\u662F\u5728\u5B83\u5185\u90E8\u8D26\u6237\u505A\u4E86\u4E00\u6B21\u8BB0\u8D26\u800C\u5DF2\uFF0C\u5DE5\u5546\u94F6\u884C\u7684\u8D44\u91D1\u5E76\u672A\u51CF\u5C11\u3002\u4F46\u662F\u5EFA\u884C\u548C\u5DE5\u884C\u4E4B\u95F4\u4E00\u5B9A\u4F1A\u53D1\u751F\u5B9E\u9645\u7684\u8D44\u91D1\u5212\u8F6C\u4EE5\u6E05\u507F\u503A\u6743\u503A\u52A1\u5173\u7CFB\uFF0C\u6240\u4EE5\u6E05\u7B97\u53CD\u6620\u7684\u662F\u4EA4\u6613\u7684\u8D44\u91D1\u6D41\u3002\u94F6\u884C\u4E4B\u95F4\u53EF\u4EE5\u6BCF\u5929\u6E05\u7B97\u4E00\u6B21\uFF0C\u4E5F\u53EF\u4EE5\u9694\u4E00\u6BB5\u65F6\u95F4\u518D\u6E05\u7B97\uFF0C\u4E0D\u8FC7\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u7684\u6E05\u7B97\u90FD\u662F\u975E\u5B9E\u65F6\u7684\u3002</p></li></ul><p>\u73B0\u4EE3\u793E\u4F1A\u7684\u51E0\u4E4E\u6240\u6709\u5546\u4E1A\u884C\u4E3A\uFF0C\u6700\u7EC8\u90FD\u4F1A\u4EA7\u751F\u4EA4\u6613\u3002\u800C\u6240\u6709\u7684\u4EA4\u6613\uFF0C\u9664\u4E86\u7269\u7269\u4EA4\u6362\uFF0C\u6700\u7EC8\u90FD\u4F53\u73B0\u5728\u94F6\u884C\u8D26\u6237\u95F4\u7684\u8D44\u91D1\u5212\u62E8\u4E0A\uFF0C\u56E0\u6B64<strong>\u4E00\u4E2A\u56FD\u5BB6\u7684\u652F\u4ED8\u6E05\u7B97\u7CFB\u7EDF</strong>\uFF08Payment and Clearing System\uFF09<strong>\u662F\u6700\u57FA\u7840\u7684\u5DE5\u7A0B</strong>\u3002\u8FD9\u4E2A\u7CFB\u7EDF\u6D89\u53CA\u4E24\u4E2A\u95EE\u9898\uFF1A</p><ul><li>\u4FE1\u606F\u6D41\u5982\u4F55\u4F20\u9012\u2014\u2014\u5373\u5EFA\u884C\u548C\u5DE5\u884C\u4E4B\u95F4\u4EE5\u4EC0\u4E48\u65B9\u5F0F\u901A\u4FE1\uFF1F</li><li>\u8D44\u91D1\u6D41\u5982\u4F55\u6E05\u7B97\u2014\u2014\u5373\u5DE5\u884C\u4EC0\u4E48\u65F6\u5019\uFF0C\u4EE5\u4EC0\u4E48\u65B9\u5F0F\u8FD8\u7ED9\u5EFA\u884C100\u5757\uFF1F</li></ul><p>\u8FD9\u4E24\u4E2A\u95EE\u9898\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u5C31\u662F\u6211\u4EEC\u8981\u4ECB\u7ECD\u7684<strong>CNAPS</strong>\uFF08\u4E2D\u56FD\u73B0\u4EE3\u5316\u652F\u4ED8\u6E05\u7B97\u7CFB\u7EDF \uFF09</p><p>\u5230\u73B0\u4EE3\u94F6\u884C\u51FA\u73B0\u540E\uFF0C\u94F6\u884C\u95F4\u5F00\u59CB\u4E86\u4E00\u5957\u57FA\u4E8E\u201C<strong>\u540C\u4E1A\u5934\u5BF8</strong>\u201D\u7684\u6E05\u7B97\u65B9\u5F0F\u3002</p><p>\u6211\u4EEC\u6458\u5F55\u7F51\u4E0A\u7684\u4E00\u6BB5\u6E05\u7B97\u79D1\u666E\uFF1A</p><blockquote><p>\u4ECE\u4E24\u767E\u5E74\u524D\u8001\u7F57\u65AF\u67F4\u5C14\u5FB7\u6253\u901A\u4E0D\u540C\u94F6\u884C\u95F4\u94F6\u7968\u7684\u901A\u5151\u5F00\u59CB\uFF0C\u94F6\u884C\u95F4\u6E05\u7B97\u4E1A\u52A1\u5C31\u4E00\u76F4\u5EF6\u7EED\u5230\u73B0\u5728\u3002\u5F53\u5E74\uFF0C\u8001\u7F57\u65AF\u67F4\u5C14\u5FB7\u62C9\u62E2\u4E00\u5E2E\u94F6\u5E84\u7684\u638C\u67DC\u8DDF\u4ED6\u4EEC\u5546\u91CF\uFF0C\u201C\u4EE5\u540E\u6211\u7684\u5BA2\u6237\u62FF\u7740\u6211\u5BB6\u7684\u94F6\u7968\u53EF\u4EE5\u5230\u4F60\u5BB6\u53D6\u91D1\u5E01\uFF0C\u7136\u540E\u4F60\u518D\u62FF\u7740\u94F6\u7968\u5230\u6211\u5BB6\u53D6\u91D1\u5E01\uFF0C\u4F5C\u4E3A\u56DE\u62A5\u6211\u4E5F\u5141\u8BB8\u4F60\u7684\u5BA2\u6237\u5230\u6211\u5BB6\u53D6\u91D1\u5E01\u201D\u3002\u4ECE\u8FD9\u4E2A\u65F6\u5019\u5F00\u59CB\uFF0C\u6807\u5FD7\u7740\u73B0\u4EE3\u94F6\u884C\u7684\u901A\u5B58\u901A\u5151\u4E1A\u52A1\u5C31\u5F00\u59CB\u4E86\u3002\u7531\u4E8E\u5927\u591A\u65F6\u5019\u53CC\u65B9\u5BA2\u6237\u90FD\u6709\u4EA4\u53C9\uFF0C\u5B9E\u9645\u9700\u8981\u76F8\u4E92\u53D6\u7684\u91D1\u5E01\u5E76\u4E0D\u591A\uFF1A\u6BD4\u5982\u4F60\u7684\u5BA2\u6237\u5728\u6211\u8FD9\u91CC\u53D6\u4E861000\u91D1\u5E01\uFF0C\u6211\u7684\u5BA2\u6237\u5728\u4F60\u90A3\u91CC\u53D6\u4E86800\u91D1\u5E01\uFF0C\u7136\u540E\u6211\u4EEC\u4E24\u5BB6\u4E00\u6BD4\u5BF9\u8D26\u672C\u53CC\u65B9\u8BA4\u53EF\uFF0C\u4F60\u518D\u7ED9\u6211200\u91D1\u5E01\u3002\u518D\u628A\u94F6\u7968\u4E00\u4EA4\u6362\uFF0C\u6211\u4EEC\u53CC\u65B9\u7684\u8D26\u5C31\u6E05\u4E86\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u5C31\u53EB<strong>\u6E05\u7B97</strong>\uFF0C\u5176\u4E2D\u6BD4\u5BF9\u8D26\u672C\u7684\u8FC7\u7A0B\u5C31\u662F<strong>\u5BF9\u8D26</strong>\u3002</p></blockquote><p>\u8FD9\u6BB5\u79D1\u666E\u63CF\u8FF0\u4E86\u94F6\u884C\u95F4\u901A\u5B58\u901A\u5151\u7684\u4E1A\u52A1\u539F\u7406\uFF0C\u5176\u4E2D\u6709\u4E00\u4E9B\u7EC6\u8282\u9700\u8981\u8BF4\u660E\uFF1A</p><ul><li>A \u94F6\u884C\u6015 B \u94F6\u884C\u7684\u5BA2\u6237\u5728 A \u8FD9\u513F\u53D6\u4E86\u94B1\u4E4B\u540E\uFF0CB \u4E0D\u8BA4\u8D26\u3002\u6240\u4EE5\u4E3A\u4E86\u4FDD\u9669\u8D77\u89C1\uFF0C\u94F6\u884C\u4E4B\u95F4\u4F1A\u8981\u6C42\u5BF9\u65B9\u94F6\u884C\u5148\u6765\u5F00\u4E2A\u6237\u5E76\u4E14\u5B58\u4E00\u90E8\u5206\u94B1\u8FDB\u6765\u4F5C\u4E3A\u5907\u4ED8\u91D1\uFF0C\u6216\u8005\u53EB<strong>\u5B58\u6B3E\u51C6\u5907\u91D1</strong>\u3002\u8FD9\u4E2A\u5907\u4ED8\u91D1\u8D26\u6237\u662F\u4E13\u95E8\u7528\u4E8E\u6E05\u7B97\u7684\u540C\u4E1A\u5934\u5BF8\u6237\uFF1B</li><li>A \u94F6\u884C\u9700\u8981\u5728\u6240\u6709\u94F6\u884C\u90FD\u5F00\u6237\uFF0C\u624D\u80FD\u5B9E\u73B0\u901A\u5B58\u901A\u5151\uFF1B</li></ul><p>\u6211\u4EEC\u56FD\u5185\u7684\u94F6\u884C\u663E\u7136\u4E5F\u53C2\u7167\u4E86\u8FD9\u4E00\u5957\u6765\u5B9E\u73B0\u8DE8\u884C\u4EA4\u6613\uFF0C\u94F6\u884C\u4E4B\u95F4\u4E92\u76F8\u90FD\u5F00\u4E86\u5907\u4ED8\u91D1\u8D26\u6237\u3002\u6B64\u65F6\uFF0C\u6BCF\u5BB6\u94F6\u884C\u90FD\u662F\u4E00\u4E2A\u6E05\u7B97\u673A\u6784\uFF0C\u4E3A\u94F6\u884C\u95F4\u7684\u8D44\u91D1\u5F80\u6765\u505A\u6E05\u7B97\u3002</p><p>\u5230\u4E86\u4E8C\u5341\u4E00\u4E16\u7EAA\uFF0CIT\u6280\u672F\u98DE\u901F\u53D1\u5C55\uFF0C\u592E\u884C\u7684CNAPS\u4E00\u4EE3\u7CFB\u7EDF\uFF08\u5927\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF\uFF09\u4E5F\u5F00\u59CB\u8D70\u4E0A\u5386\u53F2\u821E\u53F0\u3002\u4E2D\u56FD\u7684\u652F\u4ED8\u6E05\u7B97\u6B65\u5165\u4E86\u73B0\u4EE3\u5316\u652F\u4ED8\u7CFB\u7EDFCNAPS\u7684\u65F6\u4EE3\u3002</p><h3 id="_2-\u5927\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_2-\u5927\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF" aria-hidden="true">#</a> (2) \u5927\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF</h3><p>2002\u5E74\uFF0C**\u592E\u884C\u5927\u989D\u5B9E\u65F6\u652F\u4ED8\u7CFB\u7EDF\uFF08HVPS\uFF09**\u6295\u4EA7\uFF0C\u7528\u4E8E\u5904\u7406\u540C\u57CE\u548C\u5F02\u5730\u7684\u5546\u4E1A\u94F6\u884C\u8DE8\u884C\u4E4B\u95F4\uFF08\u4E5F\u5305\u62EC\u884C\u5185\u4E00\u5B9A\u91D1\u989D\u4EE5\u4E0A\u7684\uFF09\u5927\u989D\u8D37\u8BB0\u4E1A\u52A1\u3002</p><p>2005\u5E74\uFF0C**\u592E\u884C\u5C0F\u989D\u6279\u91CF\u652F\u4ED8\u7CFB\u7EDF\uFF08BEPS\uFF09**\u6295\u4EA7\uFF0C\u7528\u6765\u5904\u7406\u540C\u57CE\u548C\u5F02\u5730\u7EB8\u51ED\u8BC1\u622A\u7559\u7684\u501F\u8BB0\u652F\u4ED8\u4E1A\u52A1\u4EE5\u53CA\u6BCF\u7B14\u91D1\u989D\u57285\u4E07\u4EE5\u4E0B\u7684\u5C0F\u989D\u8D37\u8BB0\u652F\u4ED8\u4E1A\u52A1\u3002</p><p><strong>\u5927\u5C0F\u989D\u7CFB\u7EDF\u4E4B\u95F4\u6709\u86EE\u591A\u7684\u533A\u522B</strong>\uFF1A</p><p><strong>\u5927\u5C0F\u989D\u7684\u5F00\u653E\u65F6\u95F4\u4E0D\u540C</strong></p><blockquote><p>\u5927\u989D\u7CFB\u7EDF\u662F\u5DE5\u4F5C\u65E5\u7684 8:30 ~ 17:00\uFF0C\u6240\u4EE5\u5728\u8282\u5047\u65E5\u7ECF\u5E38\u4F1A\u6536\u5230\u94F6\u884C\u901A\u77E5\u8BF4\u67D0\u4E9B\u4E1A\u52A1\u6682\u505C\u4E86\u7ECF\u5E38\u5C31\u662F\u56E0\u4E3A\u592E\u884C\u5728\u8282\u5047\u65E5\u5BF9\u5927\u989D\u7CFB\u7EDF\u505A\u7EF4\u62A4\u3002\u5C0F\u989D\u7CFB\u7EDF\u5168\u5E74\u65E0\u4F11\uFF0C7*24\u5C0F\u65F6\u5DE5\u4F5C\u3002</p></blockquote><p><strong>\u4E1A\u52A1\u5904\u7406\u4E0A\u4E0D\u540C</strong></p><blockquote><p>\u5927\u989D\u662F\u6BCF\u7B14\u4EA4\u6613\u90FD\u5B9E\u65F6\u53D1\u9001\uFF0C\u5B9E\u65F6\u6E05\u7B97\u7684\uFF0C\u6240\u4EE5\u57FA\u672C\u4E0A\u80FD\u5B9E\u65F6\u5230\u8D26\uFF0C\u8DE8\u884C\u8D44\u91D1\u96F6\u5728\u9014\u3002</p></blockquote><blockquote><p>\u5C0F\u989D\u7CFB\u7EDF\u662F\u5728\u6536\u96C6\u82E5\u5E72\u7B14\u4EA4\u6613\u540E\u6253\u4E00\u4E2A\u5305\u7EDF\u4E00\u5904\u7406\uFF0C\u5B9A\u65F6\u6E05\u7B97\u3002\u6240\u4EE5\uFF0C\u7528\u5C0F\u989D\u7CFB\u7EDF\u8F6C\u8D26\u7ECF\u5E38\u8981\u51E0\u5206\u949F\u751A\u81F3\u534A\u4E2A\u5C0F\u65F6\u624D\u80FD\u5230\u8D26\uFF0C\u94F6\u884C\u95F4\u5934\u5BF8\u4EA4\u5272\u4E5F\u662F\u975E\u5B9E\u65F6\u7684\u3002</p></blockquote><blockquote><p>\u5C3D\u7BA1\u7406\u8BBA\u4E0A\u8DE8\u884C\u8F6C\u8D26\u4E1A\u52A1\u4E0D\u7BA1\u7528\u5927\u989D\u8FD8\u662F\u5C0F\u989D\uFF0C\u4E00\u822C\u5728\u51E0\u5206\u949F\u5185\u90FD\u80FD\u5230\u8D26\uFF0C\u4F46\u662F\u56E0\u4E3A\u8981\u7ECF\u8FC7\u592E\u884C\uFF0C\u6240\u4EE5\u5728\u8FD9\u4E00\u65F6\u671F\u57FA\u672C\u6CA1\u6709\u94F6\u884C\u6562\u5411\u5BA2\u6237\u627F\u8BFA\u8D44\u91D1\u591A\u4E45\u80FD\u5230\u8D26\u3002</p></blockquote><p><strong>\u91D1\u989D\u4E0D\u540C</strong></p><blockquote><p>\u5927\u989D\u7CFB\u7EDF\u6CA1\u6709\u91D1\u989D\u9650\u5236\uFF0C\u5C0F\u989D\u7CFB\u7EDF\u652F\u6301\u7684\u5355\u7B14\u91D1\u989D\u4E0A\u9650\u662F5\u4E07\u5143\u3002</p></blockquote><blockquote><p>\u4ECE\u7528\u9014\u4E0A\u8BB2\uFF0C\u5927\u989D\u7CFB\u7EDF\u4FA7\u91CD\u4E8E\u8D44\u91D1\u8F6C\u79FB\u7684\u65F6\u6548\u6027\uFF0C\u4E3B\u8981\u7528\u4E8E\u8D44\u672C\u5E02\u573A\u3001\u8D27\u5E01\u5E02\u573A\u4EA4\u6613\u548C\u5927\u989D\u8D38\u6613\u8D44\u91D1\u7ED3\u7B97\u3002\u5C0F\u989D\u7CFB\u7EDF\u5BF9\u6570\u636E\u541E\u5410\u91CF\u8981\u6C42\u8F83\u9AD8\uFF0C\u4E3B\u8981\u7528\u4E8E\u5C0F\u989D\u8D38\u6613\u652F\u4ED8\u548C\u4E2A\u4EBA\u6D88\u8D39\u670D\u52A1\u3002</p></blockquote><h3 id="_3-\u8D85\u7EA7\u7F51\u94F6" tabindex="-1"><a class="header-anchor" href="#_3-\u8D85\u7EA7\u7F51\u94F6" aria-hidden="true">#</a> (3) \u8D85\u7EA7\u7F51\u94F6</h3><p>2013\u5E7410\u67086\u65E5\uFF0C\u592E\u884C\u7684\u7B2C\u4E8C\u4EE3\u652F\u4ED8\u7CFB\u7EDF\u6B63\u5F0F\u6295\u4EA7\u8FD0\u884C\uFF0C\u5176\u4E2D\u5305\u62EC2010\u5E74\u5C31\u63A8\u51FA\u7684\u7F51\u4E0A\u652F\u4ED8\u8DE8\u884C\u6E05\u7B97\u7CFB\u7EDF\uFF08\u4FD7\u79F0\u201C\u8D85\u7EA7\u7F51\u94F6\u201D\uFF09\u3002\u8D85\u7EA7\u7F51\u94F6\u662F\u5BF9\u5927\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF\u7684\u4E00\u4E2A\u8865\u5145\uFF0C\u6709\u4E24\u5927\u4EAE\u70B9\uFF1A</p><ul><li><strong>\u63A5\u5165\u673A\u6784\u4E0D\u518D\u9650\u4E8E\u94F6\u884C</strong>\u3002\u652F\u4ED8\u5B9D\u3001\u8D22\u4ED8\u901A\u7B49\u7B2C\u4E09\u65B9\u652F\u4ED8\u4E5F\u53EF\u4EE5\u63A5\u5165\uFF0C\u6240\u4EE5\u6709\u7684\u7B2C\u4E09\u65B9\u652F\u4ED8\u7ED9\u5546\u6237\u63D0\u4F9B\u7684\u63D0\u73B0\u4EE3\u53D1\u529F\u80FD\u5C31\u662F\u57FA\u4E8E\u8D85\u7EA7\u7F51\u94F6\u505A\u7684\u3002</li><li><strong>7*24\u5C0F\u65F6\u5B9E\u65F6\u5230\u8D26\uFF0C\u5355\u7B14\u4E0A\u96505\u4E07\u5143</strong>\u3002\u8FD9\u5C31\u76F8\u5F53\u4E8E\u5728\u975E\u5DE5\u4F5C\u65E5\u975E\u8425\u4E1A\u65F6\u95F4\u589E\u52A0\u4E86\u4E00\u79CD\u5927\u989D\u652F\u4ED8\u7CFB\u7EDF\u7279\u6027\u7684\u6E20\u9053\u4E86\u3002\u53EA\u4E0D\u8FC7\u91D1\u989D\u9650\u5236\u662F\u8DDF\u5C0F\u989D\u7CFB\u7EDF\u4E00\u81F4\u7684\u3002</li></ul><h3 id="_4-\u94F6\u884C\u7684\u5404\u79CD\u6E20\u9053" tabindex="-1"><a class="header-anchor" href="#_4-\u94F6\u884C\u7684\u5404\u79CD\u6E20\u9053" aria-hidden="true">#</a> (4) \u94F6\u884C\u7684\u5404\u79CD\u6E20\u9053</h3><p>\u6709\u4E86\u5927\u5C0F\u989D\u7CFB\u7EDF\u548C\u8D85\u7EA7\u7F51\u94F6\uFF0C\u94F6\u884C\u7684\u8DE8\u884C\u4E1A\u52A1\u5C31\u6709\u610F\u601D\u4E86\u3002<strong>\u94F6\u884C\u5BF9\u4E0D\u540C\u7684\u5BA2\u7FA4\u548C\u4E0D\u540C\u7684\u573A\u666F\uFF0C\u505A\u4E86\u4E0D\u540C\u7684\u6E20\u9053</strong>\u3002\u6211\u4EEC\u6700\u7ECF\u5E38\u63A5\u89E6\u7684\u5927\u6982\u5C31\u662F\u94F6\u884C\u67DC\u53F0\u3001\u624B\u673A\u7F51\u94F6\u3001POS\u673A\u548CATM\u673A\u3002</p><p>\u51FA\u4E8E\u5404\u79CD\u5546\u4E1A\u76EE\u7684\uFF0C\u4E0D\u540C\u6E20\u9053\u7684\u4E1A\u52A1\u91C7\u7528\u4E0D\u540C\u7684\u6E05\u7B97\u7CFB\u7EDF\u6765\u5B9E\u73B0\u8DE8\u884C\u4EA4\u6613\u3002\u5927\u4F53\u5982\u4E0B\uFF1A</p><blockquote><p><strong>\u94F6\u884C\u67DC\u53F0</strong>\uFF1A\u76F4\u63A5\u7528\u5927\u5C0F\u989D\u7CFB\u7EDF\uFF1B</p><p><strong>\u624B\u673A\u7F51\u94F6</strong>\uFF1A\u5927\u5C0F\u989D\u7CFB\u7EDF + \u8D85\u7EA7\u7F51\u94F6\uFF1B</p><p><strong>ATM</strong>\uFF1A\u4FE1\u606F\u6D41\u7531\u94F6\u8054\u5904\u7406\uFF0C\u5373\u94F6\u8054CUPS\u4E3A\u94F6\u884C\u95F4\u4EA4\u6613\u63D0\u4F9B\u6307\u4EE4\u7684\u8F6C\u63A5\u548C\u6E05\u5206\uFF1B\u8D44\u91D1\u6D41\u5219\u7531\u94F6\u8054\u901A\u8FC7\u5927\u989D\u7CFB\u7EDF\u5B8C\u6210\u94F6\u884C\u95F4\u7684\u8D44\u91D1\u5212\u62E8\uFF1B</p><p><strong>POS</strong>\uFF1A\u8DDFATM\u7C7B\u4F3C\uFF0C\u7531\u94F6\u8054\u5904\u7406\u4FE1\u606F\u6D41\u3002\u4E0D\u8FC7\u8D44\u91D1\u6D41\u5206\u4E24\u90E8\u5206\uFF0C\u53D1\u5361\u884C\u548C\u6536\u5355\u884C\u4E4B\u95F4\u7684\u8D44\u91D1\u5212\u62E8\u7531\u94F6\u8054\u901A\u8FC7\u5927\u989D\u7CFB\u7EDF\u5B8C\u6210\uFF1B\u6536\u5355\u884C\u4E0E\u5546\u6237\u8D26\u6237\u7684\u8D44\u91D1\u5212\u62E8\u7531\u94F6\u8054\u901A\u8FC7\u5C0F\u989D\u7CFB\u7EDF\u5B8C\u6210\u3002</p></blockquote><ul><li>\u624B\u673A\u7F51\u94F6</li></ul><p>\u624B\u673A\u7F51\u94F6\u662F\u8FD1\u51E0\u5E74\u5F00\u59CB\u6D41\u884C\u7684\u6E20\u9053\u3002\u6BD4\u5982\u5EFA\u884C\u624B\u673A\u94F6\u884C\u7684\u8DE8\u884C\u8F6C\u8D26\uFF0C\u6211\u4EEC\u80FD\u770B\u5230\u6709\u666E\u901A\u548C\u52A0\u6025\u4E24\u79CD\u65B9\u5F0F\u3002</p><p><img src="http://122.9.159.116:5244/d/ecloud180/images/blogImage/a42d62237b4b7489ff3be49a1af92db1.png" alt="img" loading="lazy"></p><p>\u666E\u901A\u8DE8\u884C\u8F6C\u8D26\u90FD\u662F\u8D70\u5927\u5C0F\u989D\u7CFB\u7EDF\uFF0C\u52A0\u6025\u8D70\u8D85\u7EA7\u7F51\u94F6\u3002</p><p><img src="http://122.9.159.116:5244/d/ecloud180/images/blogImage/7e0c5147af79e2e87c0a9a9ac3745943.png" alt="img" loading="lazy"></p><ul><li>POS\u673A\u5237\u5361</li></ul><p>POS\u673A\u5237\u5361\u7684\u53C2\u4E0E\u65B9\u6BD4\u8F83\u591A\u3002\u4EE5\u4F60\u5728\u661F\u5DF4\u514B\u5237\u5361\u4E3A\u4F8B\uFF1A</p><blockquote><p>\u6301\u5361\u4EBA\u2014\u2014\u4F60</p><p>\u53D1\u5361\u884C\u2014\u2014\u4F60\u529E\u8FD9\u5F20\u5361\u7684\u94F6\u884C\uFF0C\u6211\u4EEC\u5047\u8BBE\u662F\u5DE5\u884C</p><p>\u5546\u6237\u2014\u2014\u661F\u5DF4\u514B\uFF0C\u6211\u4EEC\u5047\u8BBE\u661F\u5DF4\u514B\u7684\u8D26\u6237\u5F00\u5728\u5EFA\u884C</p><p>\u6536\u5355\u884C\u2014\u2014\u661F\u5DF4\u514B\u7684\u5237\u5361\u673A\u7684\u5F52\u5C5E\u94F6\u884C\uFF0C\u5047\u8BBE\u4E5F\u662F\u5EFA\u884C</p><p>\u8F6C\u63A5\u673A\u6784\u2014\u2014\u94F6\u8054</p><p>\u6E05\u7B97\u673A\u6784\u2014\u2014\u94F6\u8054\u548C\u4EBA\u884C</p></blockquote><p>\u4E8E\u662F\uFF0C\u5F53\u4F60\u7528\u5DE5\u884C\u5361\u5728\u661F\u5DF4\u514B\u6D88\u8D39\u7684\u65F6\u5019\uFF0C\u8D44\u91D1\u6D41\u548C\u73B0\u91D1\u6D41\u662F\u8FD9\u6837\u53D1\u751F\u7684\uFF1A</p><blockquote><p>\u4F60\u5728\u6536\u5355\u884C\uFF08\u5EFA\u884C\uFF09\u7684POS\u673A\u4E0A\u5237\u5361\u6D88\u8D39\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u5EFA\u884C\u5C06\u6D88\u8D39\u62A5\u6587\u53D1\u9001\u7ED9\u94F6\u8054\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u4EA4\u6613\u7CFB\u7EDF\u8BB0\u5F55\u4EA4\u6613\u6570\u636E\uFF0C\u5C06\u6D88\u8D39\u62A5\u6587\u7ED9\u4F60\u7684\u53D1\u5361\u884C\uFF08\u5DE5\u884C\uFF09\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u5DE5\u884C\u4ECE\u4F60\u7684\u5361\u4E2D\u5B9E\u65F6\u6263\u8D39\uFF0C\u5B8C\u6210\u5B9E\u65F6\u7ED3\u7B97\uFF0C\u5E76\u56DE\u590D\u62A5\u6587\u7ED9\u94F6\u8054\uFF08\u8D44\u91D1\u6D41\uFF09</p><p>\u94F6\u8054\u66F4\u65B0\u4EA4\u6613\u6570\u636E\uFF0C\u56DE\u590D\u62A5\u6587\u7ED9\u5EFA\u884C\u7684POS\u673A\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u5728\u5176\u6E05\u7B97\u7CFB\u7EDF\u5B8C\u6210\u6E05\u5206\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u901A\u8FC7\u5927\u989D\u652F\u4ED8\u7CFB\u7EDF\uFF0C\u5B8C\u6210\u5DE5\u884C\u4E0E\u5EFA\u884C\u6E05\u7B97\u8D26\u6237\u7684\u8D44\u91D1\u5212\u62E8\uFF08\u8DE8\u884C\u6E05\u7B97\uFF09\uFF08\u8D44\u91D1\u6D41\uFF09</p><p>\u94F6\u8054\u901A\u8FC7\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF\u6216\u5F53\u5730\u7968\u636E\u4EA4\u6362\u7CFB\u7EDF\uFF0C\u5B8C\u6210\u5EFA\u884C\u548C\u661F\u5DF4\u514B\u7ED3\u7B97\u8D26\u6237\u7684\u8D44\u91D1\u5212\u62E8\uFF08\u6536\u5355\u6E05\u7B97\uFF09\uFF08\u8D44\u91D1\u6D41\uFF09</p></blockquote><p>\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u94F6\u8054\u63D0\u4F9B\u4E24\u79CD\u6E05\u7B97\uFF1A</p><ul><li>\u5EFA\u884C\u548C\u5DE5\u884C\u7684\u6E05\u7B97\u53EB**\u201C\u8DE8\u884C\u6E05\u7B97\u201D**\uFF1B</li><li>\u5EFA\u884C\u548C\u661F\u5DF4\u514B\u7684\u5EFA\u884C\u8D26\u6237\u4E4B\u95F4\u7684\u6E05\u7B97\u53EB**\u201C\u6536\u5355\u6E05\u7B97\u201D**\uFF1B</li></ul><p>\u65E0\u8BBA\u662F\u8DE8\u884C\u6E05\u7B97\u8FD8\u662F\u6536\u5355\u6E05\u7B97\uFF0C\u94F6\u8054\u90FD\u662F\u4F5C\u4E3ACNAPS\u7684\u4E00\u4E2A\u7279\u8BB8\u53C2\u4E0E\u8005\uFF0C\u4F7F\u7528\u5927\u5C0F\u989D\u652F\u4ED8\u6E05\u7B97\u7CFB\u7EDF\uFF0C\u5B8C\u6210\u94F6\u884C\u5361\u4E1A\u52A1\u7684\u8D44\u91D1\u5212\u62E8\u3002\u672C\u8D28\u4E0A\uFF0C\u94F6\u8054\u63D0\u4F9B\u4EA4\u6613\u8F6C\u63A5\u3001\u6E05\u5206\u548C\u5BF9\u8D26\uFF0C\u4EBA\u884C\u63D0\u4F9B\u7ED3\u7B97\u3002</p><ul><li>ATM\u8DE8\u884C\u53D6\u6B3E</li></ul><p>ATM\u8DE8\u884C\u53D6\u6B3E\u548CPOS\u673A\u662F\u7C7B\u4F3C\u7684\uFF0C\u56E0\u4E3A\u6CA1\u6709\u5546\u6237\u53C2\u4E0E\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u6536\u5355\u6E05\u7B97\uFF0C\u8FC7\u7A0B\u66F4\u4E3A\u7B80\u5355\u3002</p><p>\u56DE\u5230\u6587\u7AE0\u6700\u5F00\u5934\u7684\u4F8B\u5B50\uFF1A\u4F60\u62FF\u7740\u4E00\u5F20\u5DE5\u884C\u5361\u53BB\u5EFA\u884C\u7684ATM\u53D6\u4E86100\u5143\uFF0C\u8FD9\u4E2A\u8DE8\u884C\u4E1A\u52A1\u5728CNAPS\u4F53\u7CFB\u4E2D\u7684\u8FC7\u7A0B\u5982\u4E0B\uFF1A</p><blockquote><p>\u4F60\u5728\u5EFA\u884CATM\u4E0A\u8F93\u5165100\u5143\u6309\u4E0B\u53D6\u6B3E\u6309\u94AE\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u5EFA\u884C\u5C06\u62A5\u6587\u4FE1\u606F\u53D1\u9001\u7ED9\u94F6\u8054\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u4EA4\u6613\u7CFB\u7EDF\u8BB0\u5F55\u4EA4\u6613\u6570\u636E\uFF0C\u5C06\u62A5\u6587\u7ED9\u4F60\u7684\u53D1\u5361\u884C\uFF08\u5DE5\u884C\uFF09\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u5DE5\u884C\u4ECE\u4F60\u7684\u5361\u4E2D\u5B9E\u65F6\u6263\u8D39\uFF0C\u5B8C\u6210\u5B9E\u65F6\u7ED3\u7B97\uFF0C\u5E76\u56DE\u590D\u62A5\u6587\u7ED9\u94F6\u8054\uFF08\u8D44\u91D1\u6D41\uFF09</p><p>\u94F6\u8054\u66F4\u65B0\u4EA4\u6613\u6570\u636E\uFF0C\u56DE\u590D\u62A5\u6587\u7ED9\u5EFA\u884CATM\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u5728\u5176\u6E05\u7B97\u7CFB\u7EDF\u5B8C\u6210\u6E05\u5206\uFF08\u4FE1\u606F\u6D41\uFF09</p><p>\u94F6\u8054\u901A\u8FC7\u5927\u989D\u652F\u4ED8\u7CFB\u7EDF\uFF0C\u5B8C\u6210\u5DE5\u884C\u4E0E\u5EFA\u884C\u6E05\u7B97\u8D26\u6237\u7684\u8D44\u91D1\u5212\u62E8\uFF08\u8DE8\u884C\u6E05\u7B97\uFF09\uFF08\u8D44\u91D1\u6D41\uFF09</p></blockquote><p>\u5546\u4E1A\u94F6\u884C\u5728CNAPS\u4F53\u7CFB\u4E2D\uFF0C</p><ul><li><strong>\u884C\u5185\u7684\u4EA4\u6613</strong></li></ul><p>\u7531\u5404\u4E2A\u94F6\u884C\u7684\u884C\u5185\u4E1A\u52A1\u7CFB\u7EDF\u6765\u81EA\u884C\u89E3\u51B3\u4FE1\u606F\u6D41\u548C\u8D44\u91D1\u6D41\u95EE\u9898\uFF1B</p><ul><li><strong>\u8DE8\u884C\u7684\u4EA4\u6613\u5206\u6E20\u9053\u5904\u7406</strong></li></ul><p>\u67DC\u53F0\u548C\u7F51\u94F6\u7B49\u6E20\u9053\uFF0C\u5546\u4E1A\u94F6\u884C\u76F4\u63A5\u76F4\u8FDE\u592E\u884C\u7684\u5927\u5C0F\u989D\u4EE5\u53CA\u8D85\u7EA7\u7F51\u94F6\u6765\u89E3\u51B3\u4FE1\u606F\u6D41\u548C\u8D44\u91D1\u6D41\u95EE\u9898\uFF1B</p><p>ATM\u548CPOS\u6E20\u9053\uFF0C\u5219\u662F\u7531\u94F6\u8054\u7684CUPS\u6765\u5BF9\u63A5\u5404\u5927\u5546\u4E1A\u94F6\u884C\u505A\u652F\u4ED8\u8F6C\u63A5\uFF0C\u89E3\u51B3\u4FE1\u606F\u6D41\u95EE\u9898\uFF1B\u94F6\u8054\u540C\u65F6\u63D0\u4F9B\u6E05\u5206\u548C\u5BF9\u8D26\u670D\u52A1\uFF0C\u5BF9\u5B8C\u8D26\u4E4B\u540E\u518D\u8C03\u7528\u592E\u884C\u5927\u5C0F\u989D\u7CFB\u7EDF\u89E3\u51B3\u8D44\u91D1\u6D41\u95EE\u9898\uFF1B</p><p><strong>|</strong> <strong>\u5E38\u89C1\u95EE\u9898</strong></p><p>\u4E86\u89E3\u4E86CNAPS\uFF0C\u6211\u4E5F\u7EC8\u4E8E\u77E5\u9053\u4E3A\u4EC0\u4E48\u5F88\u591A\u8D44\u91D1\u64CD\u4F5C\u4E0D\u80FD\u5728\u8282\u5047\u65E5\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\u7684\u65F6\u5019\u505A\u4E86\u3002</p><p>1\uFF09<strong>\u4E3A\u4EC0\u4E48\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\uFF0C\u65E0\u6CD5\u901A\u8FC7\u7F51\u94F6\u8DE8\u884C\u8F6C\u8D26\uFF1F</strong></p><p>\u7B54\uFF1A\u56E0\u4E3A\u7F51\u94F6\u8DE8\u884C\u8F6C\u8D26\uFF0C\u9700\u8981\u7528\u5230\u592E\u884C\u7684\u5927\u5C0F\u989D\u7CFB\u7EDF\u548C\u8D85\u7EA7\u7F51\u94F6\u3002</p><p>2\uFF09<strong>\u4E3A\u4EC0\u4E48ATM\u548CPOS\u673A\u4E0D\u53D7\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\u7684\u5F71\u54CD\uFF1F</strong></p><p>\u7B54\uFF1A\u5373\u4FBF\u5728\u5927\u989D\u548C\u5C0F\u989D\u652F\u4ED8\u7CFB\u7EDF\u5173\u95ED\u7684\u60C5\u51B5\u4E0B\uFF08\u4F8B\u5982\u6625\u8282\u5347\u7EA7\uFF09\uFF0C\u4E5F\u4F1A\u4E0D\u5F71\u54CD\u5230POS\u7684\u8DE8\u884C\u5237\u5361\u548CATM\u8DE8\u884C\u53D6\u6B3E\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u4EA4\u6613\u53EA\u6D89\u53CA\u4FE1\u606F\u6D41\uFF0C\u53EF\u4EE5\u901A\u8FC7\u94F6\u8054CUPS\u5B9E\u65F6\u8F6C\u63A5\u3002\u6D89\u53CA\u5B9E\u9645\u8D44\u91D1\u6D41\u8F6C\u7684\u8D44\u91D1\u5212\u62E8\u8FC7\u7A0B\u662F\u53EF\u4EE5\u5EF6\u8FDF\u8FDB\u884C\u7684\u3002</p><p>3\uFF09<strong>\u4E3A\u4EC0\u4E48\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\uFF0C\u6709\u4E9BP2P\u516C\u53F8\u7684\u63D0\u73B0\u5C31\u4E0D\u80FD\u63D0\u4E86\uFF1F</strong></p><p>\u7B54\uFF1AP2P\u516C\u53F8\u7684\u63D0\u73B0\u5927\u591A\u662F\u901A\u8FC7\u7B2C\u4E09\u65B9\u652F\u4ED8\u516C\u53F8\u7684\u4EE3\u53D1\u4E1A\u52A1\u6765\u5B9E\u73B0\u3002\u7B2C\u4E09\u65B9\u652F\u4ED8\u7684\u4EE3\u53D1\u4E1A\u52A1\u4E5F\u6709\u4E00\u4E9B\u662F\u4F9D\u8D56\u592E\u884C\u7684\u5927\u5C0F\u989D\u548C\u8D85\u7EA7\u7F51\u94F6\u6765\u5B9E\u73B0\u7684\u3002\u6240\u4EE5\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\u4E5F\u53EF\u80FD\u4F1A\u5F71\u54CDP2P\u3002</p><p>4\uFF09<strong>\u4E3A\u4EC0\u4E48\u592E\u884C\u7CFB\u7EDF\u5347\u7EA7\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u8FD8\u80FD\u5F80\u652F\u4ED8\u5B9D\u5145\u503C\uFF1F</strong></p><p>\u7B54\uFF1A\u652F\u4ED8\u516C\u53F8\u7684\u5145\u503C\u4E1A\u52A1\u5927\u591A\u662F\u901A\u8FC7\u8DDF\u8BB8\u591A\u5BB6\u94F6\u884C\u5355\u72EC\u5F00\u53D1\u5FEB\u6377\u652F\u4ED8\u63A5\u53E3\u6765\u5B9E\u73B0\u7684\uFF0C\u6240\u4EE5\u5145\u503C\u4E0D\u53D7\u592E\u884C\u7CFB\u7EDF\u5F71\u54CD\uFF0C\u4F46\u662F\u4F1A\u53D7\u5230\u5177\u4F53\u67D0\u5BB6\u94F6\u884C\u5FEB\u6377\u652F\u4ED8\u63A5\u53E3\u7EF4\u62A4\u7684\u5F71\u54CD\u3002</p><p><strong>|</strong> <strong>\u540D\u8BCD\u89E3\u91CA</strong></p><p><strong>NPC</strong> \u6E05\u7B97\u4E2D\u5FC3-\u56FD\u5BB6\u5904\u7406\u4E2D\u5FC3</p><p><strong>CCPC</strong> \u6E05\u7B97\u4E2D\u5FC3-\u5168\u56FD\u7701\u4F1A\u53CA\u6DF1\u5733\u57CE\u5E02\u5904\u7406\u4E2D\u5FC3</p><p><strong>CNAPS</strong> \u4E2D\u56FD\u73B0\u4EE3\u5316\u652F\u4ED8\u7CFB\u7EDF</p><p><strong>EIS</strong> \u5168\u56FD\u7535\u5B50\u8054\u884C\u7CFB\u7EDF</p><p><strong>CNFN</strong> \u4E2D\u56FD\u56FD\u5BB6\u91D1\u878D\u7F51\u7EDC</p><p><strong>HVPS</strong> \u592E\u884C\u5927\u989D\u5B9E\u65F6\u652F\u4ED8\u7CFB\u7EDF</p><p><strong>BEPS</strong> \u592E\u884C\u5C0F\u989D\u6279\u91CF\u652F\u4ED8\u7CFB\u7EDF</p><p><strong>CUPS</strong> \u4E2D\u56FD\u94F6\u8054\u94F6\u884C\u5361\u8DE8\u884C\u652F\u4ED8\u7CFB\u7EDF</p><p><strong>\u5934\u5BF8</strong>\uFF1A\u5934\u5BF8\u5BF9\u5E94\u7684\u82F1\u6587\u662Fposition\uFF0C\u5C31\u662F\u6307\u4ED3\u4F4D\uFF0C\u4E5F\u53EF\u4EE5\u8BF4\u662F\u8D44\u4EA7\u5B58\u91CF\u3002\u4EBA\u884C\u662F\u7ED9\u5546\u4E1A\u94F6\u884C\u8BB0\u8D26\u7684\uFF0C\u4E00\u5BB6\u94F6\u884C\u5728\u592E\u884C\u90A3\u91CC\u7684\u8D44\u91D1\u5C31\u53EB\u5934\u5BF8\uFF0C\u94F6\u884C\u53F8\u5E93\u5BF9\u5934\u5BF8\u8FDB\u884C\u7BA1\u7406\u5C31\u662F\u7BA1\u7406\u8D44\u91D1\u7684\u6D41\u52A8\u6027\uFF0C\u907F\u514D\u8D44\u91D1\u95F2\u7F6E\u3002</p><p><strong>\u652F\u4ED8</strong>\uFF1A\u652F\u4ED8\u53D1\u751F\u5728\u4ED8\u6B3E\u4EBA\u3001\u6536\u6B3E\u4EBA\u4EE5\u53CA\u5F00\u6237\u94F6\u884C\u4E4B\u95F4\u3002\u4ED8\u6B3E\u4EBA\u8D26\u9762\u4F59\u989D\u51CF\u5C11\uFF0C\u6536\u6B3E\u4EBA\u8D26\u9762\u4F59\u989D\u589E\u52A0\u5C31\u662F\u652F\u4ED8\uFF1B</p><p><strong>\u6E05\u5206</strong>\uFF1A\u6E05\u5206\u662F\u4ED8\u6B3E\u884C\u548C\u6536\u6B3E\u884C\u4EA4\u6362\u652F\u4ED8\u4FE1\u606F\uFF0C\u6839\u636E\u652F\u4ED8\u6307\u4EE4\u8BA1\u7B97\u501F\u8D37\u53CC\u65B9\u5DEE\u989D\u7684\u8FC7\u7A0B\uFF1B\u5982\u679C\u662F\u5237pos\u673A\uFF0C\u6E05\u5206\u5C31\u662F\u94F6\u8054\u505A\u7684\uFF1B</p><p><strong>\u7ED3\u7B97</strong>\uFF1A\u7ED3\u7B97\u6D89\u53CA\u4ED8\u6B3E\u4EBA\u6536\u6B3E\u4EBA\u7684\u5F00\u6237\u94F6\u884C\u548C\u592E\u884C\uFF0C\u901A\u8FC7\u592E\u884C\u5212\u8F6C\u5907\u4ED8\u91D1\u8D26\u6237\u6765\u6E05\u507F\u503A\u6743\u503A\u52A1\u5173\u7CFB\u3002</p><h2 id="_3-\u8F67\u5DEE" tabindex="-1"><a class="header-anchor" href="#_3-\u8F67\u5DEE" aria-hidden="true">#</a> 3. \u8F67\u5DEE</h2><p>\u8BFB\u97F3\uFF1Ag\xE1 ch\u0101</p><p>\u8F67\u5DEE\uFF0C\u5E76\u65E0\u4E13\u95E8\u7684\u6CD5\u5F8B\u5B9A\u4E49\uFF0C\u6C49\u8BED\u4E2D\u5BF9\u8F67\u5DEE\u7684\u76F8\u5E94\u7684\u79F0\u8C13\u8FD8\u6709\u5DEE\u989D\u8BA1\u7B97\u3001\u5BF9\u51B2\u3001\u51C0\u63D0\u7B49\u3002\u8F67\u5DEE\u7684\u6CD5\u5F8B\u673A\u5236\u662F\u5229\u7528\u62B5\u9500\u3001\u5408\u540C\u66F4\u65B0\u7B49\u6CD5\u5F8B\u5236\u5EA6\uFF0C\u6700\u7EC8\u53D6\u5F97\u4E00\u65B9\u5BF9\u53E6\u4E00\u65B9\u7684\u4E00\u4E2A\u6570\u989D\u7684\u51C0\u503A\u6743\u6216\u51C0\u503A\u52A1\uFF0C\u5982\u5E02\u573A\u4EA4\u6613\u8005\u4E4B\u95F4\uFF0C\u53EF\u80FD\u4E92\u6709\u5185\u5BB9\u76F8\u540C\uFF0C\u65B9\u5411\u76F8\u53CD\u7684\u591A\u7B14\u4EA4\u6613\uFF0C\u5728\u7ED3\u7B97\u6216\u7ED3\u675F\u4EA4\u6613\u65F6\uFF0C\u53EF\u4EE5\u5C06\u5404\u65B9\u503A\u6743\u5728\u76F8\u7B49\u6570\u989D\u5185\u62B5\u9500\uFF0C\u4EC5\u652F\u4ED8\u4F59\u989D\u3002</p><p>\u6240\u8C13\u8F67\u5DEE\uFF0C\u5C31\u662F\u6307\u4E70\u5356\u53CC\u65B9\u4E92\u76F8\u62B5\u51CF\uFF0C\u4F8B\u5982\uFF1A\u67D0\u94F6\u884C\u5728\u7ED3\u552E\u6C47\u65F6\u5148\u4E70\u5165100\u7F8E\u5143\uFF0C\u518D\u5356\u51FA30\u7F8E\u5143\uFF0C\u518D\u5356\u51FA20\u7F8E\u5143\uFF0C\u90A3\u4E48\u8FD9\u5BB6\u94F6\u884C\u8F67\u5DEE\u540E\u7684\u655E\u53E3\u662F50\u7F8E\u5143\u591A\u5934\u3002\u94F6\u884C\u9700\u8981\u5C06\u8FD950\u7F8E\u5143\u5356\u7ED9\u5916\u7BA1\u5C40\u3002\u53E6\u5916\uFF0C\u7531\u4E8E\u5546\u4E1A\u94F6\u884C\u6709\u5F88\u591A\u5206\u652F\u673A\u6784\uFF0C\u4E14\u6BCF\u4E2A\u5206\u652F\u673A\u6784\u90FD\u5728\u8FDB\u884C\u7ED3\u552E\u6C47\u4E1A\u52A1\uFF0C\u6240\u4EE5\u8981\u8FDB\u884C\u8F67\u5DEE\u4E5F\u5E76\u4E0D\u662F\u5F88\u5BB9\u6613\uFF0C\u901A\u5E38\u7684\u505A\u6CD5\u662F\u9996\u5148\u8BA9\u5206\u884C\u81EA\u884C\u8F67\u5DEE\uFF0C\u7136\u540E\u4E0E\u603B\u884C\u5E73\u4ED3\uFF0C\u8F67\u5DEE\u7684\u655E\u53E3\u5C31\u96C6\u4E2D\u5230\u603B\u884C\uFF0C\u6700\u540E\uFF0C\u603B\u884C\u5916\u6C47\u4EA4\u6613\u90E8\u95E8\u6839\u636E\u81EA\u8EAB\u7684\u655E\u53E3\u60C5\u51B5\u4E0E\u5916\u7BA1\u5C40\u5E73\u4ED3</p><p>\u4EBA\u884C\u652F\u4ED8\u7CFB\u7EDF\u6BCF\u65E5\u65E5\u7EC8\u5BF9\u5F53\u65E5\u652F\u4ED8\u4EA4\u6613\u53D1\u751F\u7684\u8D44\u91D1\u6309\u6E05\u7B97\u884C\u5206\u7C7B\uFF0C\u5E76\u8BA1\u7B97\u501F\u3001\u8D37\u65B9\u5DEE\u989D\u7684\u8FC7\u7A0B\uFF0C\u4E3A\u6700\u7EC8\u6E05\u7B97\u505A\u51C6\u5907</p><p>\u652F\u4ED8\u8F67\u5DEE\u3002\u6307\u901A\u8FC7\u964D\u4F4E\u5FC5\u987B\u652F\u4ED8\u7684\u91D1\u989D\u6765\u964D\u4F4E\u4FE1\u7528\u98CE\u9669\u3002\u5982\u679C\u652F\u4ED8\u5230\u671F\uFF0CA\u6B20B\u7684\u91D1\u989D\u5927\u4E8EB\u6B20A\u7684\u91D1\u989D\uFF0CA\u5411B\u652F\u4ED8\u6240\u6B20\u91D1\u989D\u4E4B\u5DEE\u3002 \u4E3E\u4F8B\u6765\u8BF4\uFF0C\u5982\u679CA\u6B20B10\u4E07\u5143\uFF0CB\u6B20A4\u4E07\u5143\uFF0C\u5219A\u5BF9B\u7684\u51C0\u6B20\u989D\u4E3A6\u4E07\u5143\u3002\u6CA1\u6709\u8F67\u5DEE\uFF0CB\u9700\u8981\u5411A\u652F\u4ED84\u4E07\u5143\uFF0C\u800CA\u9700\u5411B\u652F\u4ED810\u4E07\u5143\u3002\u5047\u8BBEB\u5728\u5411A\u652F\u4ED84\u4E07\u5143\u7684\u8FC7\u7A0B\u4E2D\u6CA1\u6709\u610F\u8BC6\u5230A\u4F1A\u8FDD\u7EA6\uFF0C\u5982\u679CA\u5DF2\u7ECF\u5F97\u5230B\u652F\u4ED8\u76844\u4E07\u5143\uFF0C\u5219B\u53EF\u80FD\u65E0\u6CD5\u6536\u56DE\u517610\u4E07\u5143\uFF0C\u90A3\u4E48\u5176\u4FE1\u7528\u635F\u5931\u5C06\u5927\u4E8E\u8F67\u5DEE\u540E\u76846\u4E07\u5143\u3002</p><p>\u8F67\u5DEE\u65F6\u4E00\u822C\u53EA\u662F\u505A\u8BB0\u8D26\uFF0C\u5E76\u6CA1\u6709\u8D44\u91D1\u7684\u6D41\u52A8\u3002</p><h2 id="_4-\u51B2\u6B63-\u51B2\u6B63\u91CD\u53D1" tabindex="-1"><a class="header-anchor" href="#_4-\u51B2\u6B63-\u51B2\u6B63\u91CD\u53D1" aria-hidden="true">#</a> 4. \u51B2\u6B63/\u51B2\u6B63\u91CD\u53D1</h2><p>\u5728\u652F\u4ED8\u9886\u57DF\uFF0C\u6709\u4E00\u4E2A\u4E13\u6709\u540D\u5B57\u53EB\u201C\u51B2\u6B63\u201D,\u6309\u767E\u5EA6\u767E\u79D1\u7684\u89E3\u91CA\uFF1A\u51B2\u6B63\u662F\u4E3A\u7CFB\u7EDF\u8BA4\u4E3A\u53EF\u80FD\u4EA4\u6613\u5931\u8D25\u65F6\u91C7\u53D6\u7684\u8865\u6551\u624B\u6CD5\u3002\u5373\u4E00\u7B14\u4EA4\u6613\u5728\u7EC8\u7AEF\u5DF2\u7ECF\u6807\u5FD7\u4E3A\u6210\u529F\uFF0C\u4F46\u662F\u53D1\u9001\u5230\u4E3B\u673A\u7684\u8D26\u52A1\u4EA4\u6613\u5305\u6CA1\u6709\u5F97\u5230\u54CD\u5E94\uFF0C\u5373\u7EC8\u7AEF\u4EA4\u6613\u8D85\u65F6\uFF0C\u6240\u4EE5\u4E0D\u786E\u5B9A\u8BE5\u7B14\u4EA4\u6613\u662F\u5426\u5728\u4E3B\u673A\u7AEF\u4E5F\u6210\u529F\u5B8C\u6210\uFF0C\u4E3A\u4E86\u786E\u4FDD\u7528\u6237\u7684\u5229\u76CA\uFF0C\u7EC8\u7AEF\u91CD\u65B0\u5411\u4E3B\u673A\u53D1\u9001\u8BF7\u6C42\uFF0C\u8BF7\u6C42\u53D6\u6D88\u8BE5\u7B14\u4EA4\u6613\u7684\u6D41\u6C34\uFF0C\u5982\u679C\u4E3B\u673A\u7AEF\u5DF2\u7ECF\u4EA4\u6613\u6210\u529F\uFF0C\u5219\u56DE\u6EDA\u4EA4\u6613\uFF0C\u5426\u5219\u4E0D\u5904\u7406\uFF0C\u7136\u540E\u5C06\u5904\u7406\u7ED3\u679C\u8FD4\u56DE\u7ED9\u7EC8\u7AEF\u3002</p><p>\u5982:</p><blockquote><p>\u6309\u7167\u76F8\u5173\u7684\u6807\u51C6\uFF0C\u5F53POS\u5237\u5361\u8BBE\u5907\u53D1\u51FA\u4E00\u6B21\u6263\u6B3E\u8BF7\u6C42\u7684\u65F6\u5019\uFF0C\u5982\u679CPOS\u5237\u5361\u8BBE\u5907\u56E0\u4E3A\u5404\u79CD\u539F\u56E0\u672A\u80FD\u83B7\u5F97\u672C\u6B21\u5237\u5361\u8BF7\u6C42\u7684\u7ED3\u679C\uFF0C\u5219\u9700\u8981\u53D1\u51FA\u4E00\u6B21\u201C\u51B2\u6B63\u201D\u8BF7\u6C42\uFF0C\u51B2\u6B63\u5C31\u662F\u544A\u8BC9\u670D\u52A1\u5668\uFF0C\u5982\u679C\u521A\u624D\u7684\u4EA4\u6613\u6210\u529F\u4E86\uFF0C\u5C31\u53D6\u6D88\u6389\uFF0C\u7136\u540E\u8FD4\u56DE\u51B2\u6B63\u6210\u529F\u3002\u5982\u679C\u6CA1\u6709\u6210\u529F\uFF0C\u6216\u8005\u670D\u52A1\u5668\u4E5F\u6CA1\u53D1\u73B0\u6709\uFF0C\u90A3\u4E48\u5C31\u76F4\u63A5\u51B2\u6B63\u6210\u529F\u3002\u5F53\u7136\uFF0C\u5982\u679C\u4EA4\u6613\u6210\u529F\uFF0C\u4F46\u662F\u53D6\u6D88\u5931\u8D25\uFF0C\u90A3\u4E48\u670D\u52A1\u5668\u9700\u8981\u8FD4\u56DE\u201C\u51B2\u6B63\u5931\u8D25\u201D\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5C31\u53EA\u80FD\u4EBA\u5DE5\u529E\u7406\u201C\u9000\u6B3E\u201D\u4E86\u3002</p></blockquote><p>\u4ECE\u6574\u4E2A\u4EA4\u4E92\u6D41\u7A0B\u6765\u770B\uFF0C\u5176\u5B9E\u201C\u51B2\u6B63\u201D\u5C31\u7C7B\u4F3C\u9000\u6B3E\uFF0C\u53EA\u4E0D\u8FC7\u8FD9\u4E2A\u9000\u6B3E\u662F\u8BBE\u5907\u53D1\u51FA\uFF0C\u5E76\u4E14\u81EA\u52A8\u6267\u884C\u7684\u3002</p><p>\u5BF9\u4E8E\u8FD9\u4E00\u6BB5\u89E3\u91CA\uFF0C\u53EF\u4EE5\u5206\u4E3A\u4E94\u4E2A\u90E8\u5206\u8FDB\u884C\uFF1A</p><p>\u7B2C\u4E00\u90E8\u5206\uFF1A\u7528\u6237A\u5728\u624B\u673A\u91CC\u8FDB\u884C\u4E00\u7B14\u4EA4\u6613\u64CD\u4F5C\uFF0C\u624B\u673A\u91CC\u7684\u8F6F\u4EF6\u6216\u662F\u77ED\u4FE1\u5DF2\u7ECF\u63D0\u793A\uFF0C\u663E\u793A\u8BE5\u7B14\u4EA4\u6613\u5DF2\u6210\u529F\u6216\u8005\u5DF2\u6263\u6B3E\uFF1B\u4E5F\u5C31\u662F\u767E\u79D1\u91CC\u63D0\u5230\u7684\u201C\u4E00\u7B14\u4EA4\u6613\u5728\u7EC8\u7AEF\u5DF2\u7ECF\u6807\u5FD7\u4E3A\u6210\u529F\u201D\u3002</p><p>\u7B2C\u4E8C\u90E8\u5206\uFF1A\u4F46\u662F\uFF0C\u8FD9\u7B14\u4EA4\u6613\u9700\u8981\u901A\u8FC7\u7F51\u7EDC\u4F20\u8F93\u4EA4\u6613\u4FE1\u606F\uFF0C\u53EF\u80FD\u56E0\u4E3A\u67D0\u4E9B\u539F\u56E0\uFF0C\u8BE5\u7B14\u4EA4\u6613\u5E76\u4E0D\u80FD\u767E\u5206\u4E4B\u767E\u786E\u5B9A\u5DF2\u7ECF\u4F20\u8F93\u5230\u4E3B\u673A\u7AEF\uFF08\u4F8B\u5982\uFF1A\u94F6\u884C\u7684\u670D\u52A1\u5668\u4E3B\u673A\uFF09\uFF1B\u4E5F\u5C31\u662F\u767E\u79D1\u91CC\u63D0\u5230\u7684\u201C\u4E0D\u786E\u5B9A\u8BE5\u7B14\u4EA4\u6613\u662F\u5426\u5728\u4E3B\u673A\u7AEF\u4E5F\u6210\u529F\u5B8C\u6210\u201D\u3002</p><p>\u7B2C\u4E09\u90E8\u5206\uFF1A\u6240\u4EE5\uFF0C\u65E0\u8BBA\u8BE5\u7B14\u4EA4\u6613\u662F\u5426\u6210\u529F\uFF0C\u90FD\u8BF7\u6C42\u53D6\u6D88\u8BE5\u7B14\u4EA4\u6613\u7684\u6D41\u6C34\uFF1B\u4E5F\u5C31\u662F\u767E\u79D1\u91CC\u63D0\u5230\u7684\u201C\u4E3A\u4E86\u786E\u4FDD\u7528\u6237\u7684\u5229\u76CA\uFF0C\u7EC8\u7AEF\u91CD\u65B0\u5411\u4E3B\u673A\u53D1\u9001\u8BF7\u6C42\uFF0C\u8BF7\u6C42\u53D6\u6D88\u8BE5\u7B14\u4EA4\u6613\u7684\u6D41\u6C34\u201D\u3002</p><p>\u7B2C\u56DB\u90E8\u5206\uFF1A\u5982\u679C\u5728\u7F51\u7EDC\u4E0D\u901A\u7545\u6216\u662F\u5176\u4ED6\u539F\u56E0\u7684\u60C5\u51B5\u4E0B\uFF0C\u4F46\u4E3B\u673A\u7AEF\u8FD8\u662F\u5DF2\u7ECF\u4EA4\u6613\u6210\u529F\u4E86\uFF0C\u5219\u56DE\u6EDA\u4EA4\u6613\u3002\u56DE\u6EDA\u4EA4\u6613\u4E5F\u5C31\u662F\u8BF4\uFF0C\u8FD8\u539F\u5230\u8FD9\u7B14\u4EA4\u6613\u6210\u529F\u4E4B\u524D\u7684\u72B6\u6001\u3002\uFF08\u56DE\u6EDA\uFF1A\u6307\u7684\u662F\u7A0B\u5E8F\u6216\u6570\u636E\u5904\u7406\u9519\u8BEF\uFF0C\u5C06\u7A0B\u5E8F\u6216\u6570\u636E\u6062\u590D\u5230\u4E0A\u4E00\u6B21\u6B63\u786E\u72B6\u6001\u7684\u884C\u4E3A\u3002\uFF09\u4E5F\u5C31\u662F\u767E\u79D1\u91CC\u63D0\u5230\u7684\u201C\u5982\u679C\u4E3B\u673A\u7AEF\u5DF2\u7ECF\u4EA4\u6613\u6210\u529F\uFF0C\u5219\u56DE\u6EDA\u4EA4\u6613\u201D\u3002</p><p>\u7B2C\u4E94\u90E8\u5206\uFF1A\u5982\u679C\u5728\u7F51\u7EDC\u4E0D\u901A\u7545\u6216\u662F\u5176\u4ED6\u539F\u56E0\u7684\u60C5\u51B5\u4E0B\uFF0C\u4E3B\u673A\u7AEF\u771F\u7684\u6CA1\u6709\u4EA4\u6613\u6210\u529F\uFF0C\u5219\u4E0D\u8FDB\u884C\u5904\u7406\uFF0C\u7136\u540E\u5C06\u6CA1\u6709\u4EA4\u6613\u6210\u529F\u7684\u7ED3\u679C\u8FD4\u56DE\u5230\u7EC8\u7AEF\u3002\u4E5F\u5C31\u662F\u767E\u79D1\u91CC\u63D0\u5230\u7684\u201C\u4E0D\u5904\u7406\uFF0C\u7136\u540E\u5C06\u5904\u7406\u7ED3\u679C\u8FD4\u56DE\u7ED9\u7EC8\u7AEF\u201D\u3002</p><p>\u4E0B\u9762\u518D\u4E3E\u4E2A\u7B80\u5355\u7684\u4F8B\u5B50\u8FDB\u884C\u8BF4\u660E\uFF1A</p><blockquote><p>\u67D0\u5929\uFF0C\u4F60\u5728\u4E2D\u56FD\u94F6\u884CATM\u673A\u4E0A\u53D6\u6B3E\u65F6\uFF0C\u672A\u6B63\u5E38\u5410\u949E\uFF0C\u800C\u63A5\u8FDE\u6536\u5230\u7684\u4E24\u6761\u94F6\u884C\u77ED\u4FE1\u3002</p><p>\u7B2C\u4E00\u6761\u662F\uFF0C\u6263\u6B3E\u4FE1\u606F\u3002\u60A8\u7684\u94F6\u884C\u5361\uFF0C\u5728\u67D0\u65E5\u67D0\u6708\u5728ATM\u673A\u53D6\u6B3E\u652F\u51FA3000\u5143\uFF0C\u4F59\u989D\u4E3AXXXX\u5143\uFF1B</p><p>\u7B2C\u4E8C\u6761\u662F\uFF0C\u51B2\u6B63\u6536\u5165\u4FE1\u606F\u3002\u60A8\u7684\u94F6\u884C\u5361\uFF0C\u5728\u67D0\u65E5\u67D0\u6708\u51B2\u6B63\u6536\u51653000\u5143\uFF0C\u4F59\u989D\u4E3AXXXX\u5143\uFF1B</p><p>\u8FD9\u4E2A\u60C5\u51B5\uFF0C\u53EF\u4EE5\u7406\u89E3\u4E3A\uFF1A\u6301\u5361\u4EBA\u5728\u8FDB\u884C\u76F8\u5173\u4EA4\u6613\u64CD\u4F5C\u540E\uFF0C\u53D6\u6B3E\u7684\u884C\u4E3A\u672A\u80FD\u5B8C\u6210\uFF0C\u4F46\u663E\u793A\u5DF2\u6263\u6B3E\uFF0C\u968F\u540E\u94F6\u884C\u505A\u4E86\u66F4\u6B63\u5206\u5F55\uFF0C\u81EA\u52A8\u5C06\u50A8\u6237\u4F59\u989D\u52A0\u56DE\u53BB\u4E86\u3002</p></blockquote>',131),e=[s];function a(l,g){return o(),t("div",null,e)}const c=p(n,[["render",a],["__file","\u4E92\u8054\u7F51\u91D1\u878D\u884C\u4E1A\u540D\u8BCD\u7B14\u8BB0.html.vue"]]);export{c as default};
